      },
      source: {
        data: data,
        parser: {
          type: 'geojson',
        },
      },
      color: '#4cfd47',
      size: 20,
      animate: true,
      state: { active: true },
      autoFit: true,
      label: {
        field: 'cityName',
        style: {
          fill: '#fff',
          fontSize: 12,
          textAnchor: 'top',
          textOffset: [0, 20],
        },
      },
      zoom: {
        position: 'bottomright',
      },
      layerMenu: {
        position: 'topright',
      },
      tooltip: {
        items: [{ field: 'cityName', alias: '名称' }],
      },
    });
  });

