            return 4;
          } else {
            return 2;
          }
        },
      },
      style: {
        opacity: 0.8,
        stroke: '#c3faff',
        strokeWidth: 1,
      },
      state: { active: { color: '#FFF684' } },
      zoom: {
        position: 'bottomright',
      },
      tooltip: {
        items: ['name'],
      },
    });
  });

