fetch('https://gw.alipayobjects.com/os/antfincdn/g5hIthhKlr/quanguoshixianweizhi.json')
  .then((response) => response.json())
  .then(({ list }) => {
    new Dot('container', {
      map: {
        type: 'amap',
        style: 'dark',
        zoom: 5,
        center: [107.4976, 32.1697],
        pitch: 0,
      },
      source: {
        data: list,
        parser: {
          type: 'json',
          coordinates: 'lnglat',
        },
      },
      size: 4,
      color: {
        field: 'style',
        value: ({ style }) => {
          if (style == 0) {
            return '#14B4C9';
          } else if (style == 1) {
            return '#3771D9';
          } else {
            return '#B8EFE2';
