        source: {
          data: flydata,
          parser: {
            type: 'json',
            coordinates: 'coord',
          },
        },
        shape: 'arc3d',
        color: '#ff6b34',
        size: 20,
        texture: 'plane',
        style: {
          textureBlend: 'replace',
